import math
import unittest
from collections import Counter

from art.framework.core.transformation.bwt.bwt import Bwt


class Test(unittest.TestCase):
    def test_bwt0_success(self):
        string = 'mississippi'
        bwt = Bwt.encode(string)
        assert bwt == ['i', 'p', 's', 's', 'm', '$', 'p', 'i', 's', 's', 'i', 'i']
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt1_success(self):
        string = 'ABRACADABRA'
        bwt = Bwt.encode(string)
        assert bwt == ['A', 'R', 'D', '$', 'R', 'C', 'A', 'A', 'A', 'A', 'B', 'B']
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt2_success(self):
        string = 'mmiissiissiippii'
        bwt = Bwt.encode(string)
        assert bwt == ['i', 'i', 'p', 's', 's', 'm', 'i', 'i', 'i', 'm', '$', 'p', 'i', 's', 's', 'i', 'i']
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt3_success(self):
        string = 'rikki-tikki-tikka'
        bwt = Bwt.encode(string)
        assert bwt == ['a', 'i', 'i', 'k', 'k', 'k', 't', 't', 'r', 'k', 'k', 'k', 'i', 'i', 'i', '$', '-', '-']
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt4_success(self):
        string = ''  # noqa
        bwt = Bwt.encode(string)
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt5_success(self):
        string = '100101101100100000110101001111'  # noqa
        bwt = Bwt.encode(string)
        print()
        print(''.join(bwt).replace('$', ''))
        result = Bwt.decode(bwt)
        print(result)
        assert result == string

    def test_bwt_empty_success(self):
        string = ''
        bwt = Bwt.encode(string)
        assert bwt == ['$']
        result = Bwt.decode(bwt)
        assert result == string

    def test_bwt_equal_success(self):
        string = 'aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa'  # noqa
        bwt = Bwt.encode(string)
        assert bwt == ['a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', 'a', '$']  # noqa
        result = Bwt.decode(bwt)
        assert result == string

    @staticmethod
    def entropy(s):
        l = float(len(s))
        return -sum(map(lambda a: (a/l)*math.log2(a/l), Counter(s).values()))

    def test_1_success(self):
        string = 'aaaaaaaaabaaaaaaaaaaaaaaaaaaaaaaaaa'  # noqa
        e = Test.entropy(string)
        assert True


if __name__ == '__main__':
    """
    """
    unittest.main()
